mapper.select:
select id, user_id, attent_count, consume_count, balance, status, create_time
from t_account_user
where id = #{id,jdbcType=INTEGER}

select t.id, t.user_id, t.attent_count, t.consume_count, t.balance,a.nick_name,a.openid from t_account_user t left join t_wechat_user a on a.id = t.user_id WHERE 1=1
AND t.user_id = #{userId}
AND t.status = #{status}
ORDER BY t.status DESC,t.id DESC
