mapper.select:
select id, user_id, user_address, contact, phone, status, create_time
from t_user_address
where id = #{id,jdbcType=INTEGER}

select t.id, t.user_id, t.user_address, t.contact, t.phone,a.nick_name,a.openid from t_user_address t left join t_wechat_user a on a.id = t.user_id WHERE 1=1
AND t.user_id = #{userId}
AND t.status = #{status}
ORDER BY t.status DESC,t.id DESC
